          {step === 2 && (
            <div className="space-y-6">
              <FormField
                control={form.control}
                name="skills"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Skills & Interests</FormLabel>
                    <FormControl>
                      <Textarea 
                        rows={4}
