              <FormField
                control={form.control}
                name="activities"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Extracurricular Activities</FormLabel>
                    <FormControl>
                      <Textarea 
                        rows={4}
                        placeholder="Clubs, volunteer work, internships, leadership roles, etc." 
                        {...field}
